mapper.select:
select id
from userinfo 
where id = #{id}

select id, username, address, tel 
from userinfo 
where id = #{userId} and pw = #{userPw}

mapper.update:
update userinfo 
set 

	pw = #{userPw}, 

username = #{userName}, address = #{userAddress}, tel = #{userTel} 
where id = #{userId}

mapper.select:
select bookmark_id, user_id, aptCode
from bookmark 
where user_id = #{userId}
